query TeamAssets($handle: String!) {
  me {
    id
    membership(team_handle: $handle) {
      id
      permissions
      __typename
    }
    __typename
  }
  team(handle: $handle) {
    id
    handle
    structured_scope_versions(archived: false) {
      max_updated_at
      __typename
    }
    in_scope_assets: structured_scopes(
      first: 650
      archived: false
      eligible_for_submission: true
    ) {
      edges {
        node {
          id
          asset_type
          asset_identifier
          instruction
          max_severity
          eligible_for_bounty
          labels(first: 100) {
            edges {
              node {
                id
                name
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    out_scope_assets: structured_scopes(
      first: 650
      archived: false
      eligible_for_submission: false
    ) {
      edges {
        node {
          id
          asset_type
          asset_identifier
          instruction
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
